import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { withStyles } from '@material-ui/styles';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import Container from '@material-ui/core/Container';
import SearchIcon from '@material-ui/icons/Search';
import Button from '@material-ui/core/Button';
import Navbar from '../components/Navbar';
import MovieGallery from '../components/MovieGallery';
import axios from 'axios';

const styles = (theme) => ({
    headContainer: {
        margin: '45px auto 10px',
        textAlign: 'center'
    },
    titleField: {
        width: '40%',
        margin: '20px 10px'
    },
    yearField: {
        width: '15%',
        margin: '20px 10px'
    },
    movieContainer: {
        maxWidth: '78%',
        margin: '10px auto 40px'
    },
    btnContainer: {
        textAlign: 'center',
        marginBottom: '20px'
    },
    buttonActions: {
        margin: '5px',
        padding: '8px 20px',
        color: '#fff',
        backgroundColor: '#ee6e73',
        '&:hover': {
            background: '#e83940'
        }
    }
});

class Index extends Component {
    state = {
        movieTitle: '',
        movieArray: ''
    };

    handleChange = (e) => {
        console.log(e.target.value);
        this.setState({
            ...this.state
        });
    };

    onSearch = () => {};

    keyPress = (e, state) => {
        if (e.keyCode == 13) {
            this.setState({
                ...this.state,
                movieTitle: e.target.value
            });
            setTimeout(() => {
                axios
                    .get(
                        'http://www.omdbapi.com/?apikey=50f7c729&s=' +
                            this.state.movieTitle +
                            '&type=series'
                    )
                    .then((res) => {
                        var movieArray = res.data.Search;
                        this.setState({
                            ...this.state,
                            movieArray: movieArray
                        });
                    });
            }, 500);
        }
    };

    render() {
        const { classes } = this.props;
        return (
            <div className="mainDiv">
                <Navbar />
                <Container maxWidth="lg" className={classes.headContainer}>
                    <Typography
                        variant="h4"
                        gutterBottom
                        className={classes.mainHeading}
                    >
                        Search for any movie
                    </Typography>
                    <TextField
                        label="Movie Title"
                        id="standard-basic"
                        name="movieTitle"
                        margin="normal"
                        className={classes.titleField}
                        onChange={this.handleChange}
                        onKeyDown={this.keyPress}
                        color="secondary"
                    />
                    <TextField
                        label="Year"
                        name="movieYear"
                        id="standard-basic"
                        margin="normal"
                        className={classes.yearField}
                        color="secondary"
                    />
                </Container>
                <Container className={classes.btnContainer}>
                    <Button
                        variant="contained"
                        color="secondary"
                        className={classes.buttonActions}
                        startIcon={<SearchIcon />}
                        onClick={this.onSearch}
                    >
                        Search Movie
                    </Button>
                </Container>
                <Container className={classes.movieContainer}>
                    <MovieGallery movieArray={this.state.movieArray} />
                </Container>
            </div>
        );
    }
}

Index.propTypes = {
    classes: PropTypes.object.isRequired
};

export default withStyles(styles)(Index);
