import React, { Component } from 'react';
import PropTypes from 'prop-types';
// import Fetch from 'isomorphic-unfetch';
import { withStyles } from '@material-ui/styles';
import Typography from '@material-ui/core/Typography';
import TextField from '@material-ui/core/TextField';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/InputLabel';
import Input from '@material-ui/core/Input';
import Container from '@material-ui/core/Container';
import Navbar from '../components/Navbar';
import MovieGallery from '../components/MovieGallery';

const styles = (theme) => ({
    headContainer: {
        margin: '45px auto',
        textAlign: 'center'
    },
    textField: {
        width: '70%',
        margin: '20px auto'
    },
    movieContainer: {
        maxWidth: '78%',
        margin: '10px auto 40px'
    }
});

class Index extends Component {
    state = {
        movieInput: ''
    };

    handleChange = (e) => {
        console.log(e.target.value);
    };

    keyPress = (e, state) => {
        if (e.keyCode == 13) {
            this.setState({
                movieInput: e.target.value + ' woodoo'
            });
            setTimeout(() => {
                console.log(state.movieInput);
            }, 500);
        }
    };

    render() {
        const { classes } = this.props;
        return (
            <div className="mainDiv">
                <Navbar />
                <Container maxWidth="lg" className={classes.headContainer}>
                    <Typography
                        variant="h4"
                        gutterBottom
                        className={classes.mainHeading}
                    >
                        Search for any movie
                    </Typography>
                    <TextField
                        label="Search movies"
                        margin="normal"
                        variant="outlined"
                        className={classes.textField}
                        onChange={this.handleChange}
                        onKeyDown={this.keyPress}
                    />
                    {/* <FormControl>
                        <InputLabel htmlFor="my-input">Movies!</InputLabel>
                        <Input
                            id="my-input"
                            aria-describedby="my-helper-text"
                            onInput={this.handleChange}
                        />
                    </FormControl> */}
                </Container>
                <Container className={classes.movieContainer}>
                    <MovieGallery />
                </Container>
            </div>
        );
    }
}

Index.propTypes = {
    classes: PropTypes.object.isRequired
};

// Index.getInitialProps = async function() {
//     const res = await fetch('http://www.omdbapi.com/?apikey=50f7c729&s=');
//     const data = await res.json();

//     return {
//         data: data
//     }
// }

export default withStyles(styles)(Index);
